import React, { useState } from 'react'
import { Link, useNavigate } from "react-router-dom";
import Badge from 'react-bootstrap/Badge'
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Modal from '../Modal';
import Cart from '../screens/Cart';
import { useCart } from './ContextReducer';
export default function Navbar() {
  let data = useCart();
  const [cartView, setCartView] = useState(false)
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login")
  }
  return (
    <div><nav className="navbar navbar-expand-lg navbar-dark bg-success " style={{boxShadow: "0px 10px 20px black", filter: 'blur(20)',position:"fixed",zIndex:"10",width:"100%"}}>
      <div className="container-fluid">
        <Link className="navbar-brand fs-1 fst-italic me-auto " to="/">GoFood</Link>
        {(localStorage.getItem("authToken")) ?
        <div className='btn bg-white text-success mx-2' style={{ position: "relative" }} onClick={() => { setCartView(true) }}>My Cart {"  "}
                {data.length > 0 ? <Badge pill className="fs-7 px-2 py-1" bg="warning" style={{ position: "absolute", marginTop: "-5px" }}>{data.length}</Badge> : null}</div>:null}
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto ">
            <li className="nav-item">
              <Link className="nav-link active fs-5" aria-current="page" to="/">Home</Link>
            </li>
            {(localStorage.getItem("authToken")) ?
              <li className="nav-item">
                <Link className='nav-link active fs-5 ' aria-current="page" to="/myOrder" >My Orders</Link>
              </li> : ""}
          </ul>
          {(!localStorage.getItem("authToken")) ?
            <div className='d-flex'>

              <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
              <Link className="btn bg-white text-success mx-1" to="/createuser">SignUp</Link>

            </div> :
            <div className='d-flex'>
              {/* <div className='btn bg-white text-success mx-2' style={{ position: "relative" }} onClick={() => { setCartView(true) }}>My Cart {"  "}
                {data.length > 0 ? <Badge pill className="fs-7 px-2 py-1" bg="warning" style={{ position: "absolute", marginTop: "-5px" }}>{data.length}</Badge> : null}</div> */}
              {cartView ? <Modal children={<Cart />} onClose={() => { setCartView(false) }}></Modal> : null}
              <div className='btn bg-white text-danger mr-2' onClick={handleLogout}>Logout</div>
            </div>}

        </div>
      </div>
    </nav></div>
  )
}
