import React from 'react'
import {useDisptachCart, useCart} from './ContextReducer'
export default function Card(props) {
    let options=props.options;
    let priceOptions=Object.keys(options)
    let foodItem=props.foodItem;
    const handleAddToCart=()=>{

    }
    return (
        <div>
            <div>
                <div className="card mt-3" style={{ "width": "14rem", "maxHeight": "360px" }}>
                    <img className="card-img-top" src={foodItem.img} alt="Card image cap" style={{height:"120px",objectFit:"fill"}}/>
                    <div className="card-body">
                        <h5 className="card-title">{foodItem.foodName}</h5>
                        {/* <p className="card-text">on the card title card's content.</p> */}
                        <div className="container w-100">
                            <select name="" id="" className="m-2 h-70 bg-success rounded">
                                {
                                    Array.from(Array(6), (e, i) => {
                                        return (
                                            <option key={i + 1} value={(i + 1)}>{i + 1}</option>
                                        )
                                    })
                                }
                            </select>
                            <select name="" id="" className="m-2 h-70 bg-success rounder">
                                {priceOptions.map((data)=>{
                                    return <option key={data} value={data}>{data}</option>
                                })}

                            </select>
                            <div className='d-inline h-100'>
                                Total Price
                            </div>

                        </div>
                        <hr></hr>
                        <button className='btn btn-succes justify-content ms-2' onClick={handleAddToCart}>Add to Cart</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
