import React from 'react'
import trash from "../trash.svg"
import { useCart, useDispatchCart } from '../components/ContextReducer';
export default function Cart() {
    let data=useCart();
    let dispatch=useDispatchCart();
    if(data.length===0){
        return(
            <div>
                <div className="m-5 w-100 text-center fs-3"> The Cart is Empty!</div>
            </div>
        )
    }
    trash.style.fill='white'
    let totalPrice=data.reduce((total,food)=>total+food.price,0)
  return (
    <div>
        <div className="container m-auto mt-5 table-responsive-sm table-responsive-md">
            <table className="table table-hover">
                <thead className="text-success fs-4">
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Name</th>
                        <th scope="col">Quantity</th>
                        <th scope="col">Option</th>
                        <th scope="col">Amount</th>
                    </tr>
                </thead>
                <tbody>
                        {data.map((food,index)=>(
                            <tr>
                                <th scope="row" key={food._id}>{index+1}</th>
                                <td>{food.name}</td>
                                <td>{food.qty}</td>
                                <td>{food.size}</td>
                                <td>{food.price}</td>
                                <td><button type="button" className="btn p-0" ><img src={trash} style={{height:"20px"}}alt="delete" onClick={()=>{dispatch({
                                    type:"REMOVE",index:index
                                })}}/></button> </td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    </div>
  )
}
